import React from 'react';
import ReactTable from 'react-table-v6';
import { Row, Col, Card, CardHeader, CardTitle, CardBody, Button, Label, Input, Badge } from 'reactstrap';
import PersonalModal from '../../components/Modal/modal';
import { useForm, Controller } from "react-hook-form";
import { getData, ActualizaS } from './function';

const content = (props) => {
    const [openModal, setOpenModal] = React.useState(false);
    const [data, setData] = React.useState([]);
    const { handleSubmit, errors, control, reset } = useForm();
    const Actualiza = async data => ActualizaS(data, setData);

    React.useEffect(() => {
        if (data.length === 0) {
            getData({ setData, setOpenModal });
        }
        setOpenModal(false);
    }, [data])

    React.useEffect(() => {
        if (openModal.id === undefined) {
            reset();
        }
    }, [openModal])

    const modalAction = () => {
        let dato = data.filter(elem => elem.ID === openModal.id)[0];
        if (dato === undefined) return ""
        return (
            <PersonalModal modal={openModal.flag} setModal={setOpenModal} title={dato.Name} size='sm'>
                <Row>
                    <Col sm={12}>
                        <Label style={{ color: "black" }}>Nombre :</Label>
                        <span> {dato.Name} </span>
                    </Col>
                    <Col sm={12}>
                        <Label style={{ color: "black" }}>Nombre :</Label>
                        <span> {dato.Status} </span>
                    </Col>
                    <Col sm={12}>
                        <form onSubmit={handleSubmit(Actualiza)}>
                            <Label for="valor" style={{ color: "black" }}>Valor :</Label>
                            {errors.valor && <Badge color="warning" pill>El campo es requerido</Badge>}
                            <Controller
                                as={Input}
                                control={control}
                                type="hidden"
                                name="id"
                                defaultValue={dato.ID}
                            />
                            <Controller
                                as={Input}
                                control={control}
                                type="number"
                                name="valor"
                                placeholder="valor"
                                autoComplete="off"
                                rules={{
                                    required: true
                                }}
                                defaultValue={dato.Value}
                            />
                            <center>
                                <Button type="submit" className="btn-round" color="danger" size="lg" >
                                    {"Actualizar"}
                                </Button>
                            </center>
                        </form>
                    </Col>
                </Row>
            </PersonalModal >
        )
    }

    const columns = [
        {
            Header: 'Secuencial',
            accessor: 'ID'
        }, {
            Header: 'Nombre',
            accessor: 'Name',
        }, {
            Header: 'Estado',
            accessor: 'Status',
        }, {
            Header: "Valor",
            accessor: 'Value'
        }, {
            Header: "Acciones",
            accessor: "actions",
            sortable: false,
            filterable: false
        }
    ]

    return (
        <>
            <div className="content">
                <Row>
                    <Col xs={12} md={12}>
                        <Card>
                            <CardHeader>
                                <CardTitle tag="h3">{"Tipos de reporte "}</CardTitle>
                            </CardHeader>
                            <CardBody>
                                <ReactTable
                                    data={data}
                                    columns={columns}
                                    filterable
                                    resizable={false}
                                    defaultPageSize={10}
                                    showPaginationBottom
                                    className="-striped -highlight"
                                />
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </div>
            {modalAction()}
        </>
    )
}

export default content;
