import React from 'react';
import swal from 'sweetalert';
import axios from 'axios';
import { Button } from 'reactstrap';

const Actions = (props) => {
    return (
        <div className="actions-right">
            <Button
                onClick={() => { props.setOpenModal({ flag: true, id: props.data }); }}
                color="info"
                size="sm"
                className="btn-icon btn-round"
            >
                <i className="tim-icons icon-alert-circle-exc" />
            </Button>
        </div >
    )
}

export const getData = async (props) => {
    axios.get("authAdmin/typeReports/GetTypeReports", {
        id: "",
    }).then((response) => {
        if (response.status) {
            let data = [];
            response.data.data.typeReports.map((elem) => {
                elem['Status'] = elem.Status === true ? "ACTIVO" : "INACTIVO";
                elem['actions'] = Actions({ setOpenModal: props.setOpenModal, data: elem.ID })
                data.push(elem);
            });
            props.setData(data);
        } else {
            swal(response.msg, "", "warning");
        }
    }).catch(function (error) {
        swal("Algo inesperado paso!", "Intenta mas tarde", "warning")
    })
}

export const ActualizaS = async (data, setData) => {
    swal({
        title: "Esta seguro?",
        text: "Esta por actualizar el valor",
        icon: "warning",
        buttons: ["Cancelar!", "Si, Actualizar!"],
    }).then((flag) => {
        if (flag === true) {
            axios.post("authAdmin/typeReports/UpdateTypeReports", {
                id: data.id,
                value: parseInt(data.valor)
            }).then(function (response) {
                swal("Exito!", "Se guardo / actualizo con exito", "success");
                setData([]);
            }).catch(function (error) {
                swal("Algo inesperado paso!", "Intenta mas tarde", "warning")
            })
        }
    });

}
const Data = () =>{
    return(
        "Data"
    )
}

export default Data;
